mapper.select:
SELECT MEMBER_ID
FROM MEMBER_INFO
WHERE CONTACT = #{id_Phone}
AND MEMBER_BIRTH = #{id_Birth}
AND E_NAME = #{id_Email}
AND E_ADDRESS = #{id_Address}

SELECT MEMBER_PW
FROM MEMBER_INFO
WHERE MEMBER_ID = #{pw_Id}
AND E_NAME = #{pw_Email}
AND E_ADDRESS = #{pw_Address}

SELECT MEMBER_NO, NICKNAME
FROM MEMBER_INFO
WHERE MEMBER_ID = #{mId}
AND MEMBER_PW = #{mPw}

SELECT MEMBER_ID
FROM MEMBER_INFO
WHERE MEMBER_ID = #{mId}

SELECT MEMBER_ID
FROM MEMBER_INFO
WHERE E_NAME = #{mEmail}
AND E_ADDRESS =#{address}
